/**
 * SF UI Library Theme
 * 
 * このファイルをインポートするだけで、LCARSカラーとフォント設定が自動的に適用されます。
 * Tailwind CSS 4.xの@themeディレクティブを使用しています。
 * 
 * 使用方法:
 * 1. このファイルをプロジェクトのグローバルCSSファイル（例: app/globals.css）にインポート
 *    @import "sf-ui-library/styles/sf-ui-theme.css";
 * 
 * 2. または、Registryからインストールした場合は:
 *    @import "@/styles/sf-ui-theme.css";
 */

@import "tailwindcss";

@theme {
  /* LCARS風カラーパレット */
  --color-lcars-blue: #4A9EFF;
  --color-lcars-orange: #FF9E4A;
  --color-lcars-red: #FF4A4A;
  --color-lcars-yellow: #FFE04A;
  --color-lcars-purple: #9E4AFF;
  --color-lcars-gray: #808080;
  --color-lcars-green: #4AFF9E;
  --color-lcars-dark: #000000;
  --color-lcars-blue-glow: #6BB5FF;
  --color-lcars-red-glow: #FF6B6B;
  --color-lcars-yellow-glow: #FFF06B;
  --color-lcars-purple-glow: #B56BFF;
  --color-lcars-gray-glow: #A0A0A0;
  --color-lcars-green-glow: #6BFFB5;

  /* カスタムフォントファミリー */
  --font-family-lcars: "Rajdhani", system-ui, -apple-system, sans-serif;
  --font-family-cli: "Share Tech Mono", monospace;
  --font-family-sf: "Orbitron", system-ui, -apple-system, sans-serif;

  /* 発光アニメーション */
  --animate-glow: glow 2s ease-in-out infinite;
  --animate-glow-pulse: glow-pulse 2s ease-in-out infinite;
  --animate-glow-intense: glow-intense 1.5s ease-in-out infinite;
  --animate-system-active: system-active 3s ease-in-out infinite;
  --animate-file-transfer: file-transfer 2s linear infinite;
  --animate-background-shift: background-shift 20s ease-in-out infinite;
  --animate-data-flow: data-flow 2s ease-in-out infinite;
}

@keyframes glow {
  0%, 100% {
    opacity: 1;
    filter: brightness(1);
  }
  50% {
    opacity: 0.8;
    filter: brightness(1.2);
  }
}

@keyframes glow-pulse {
  0%, 100% {
    opacity: 1;
    box-shadow: 0 0 5px currentColor, 0 0 10px currentColor, 0 0 15px currentColor;
  }
  50% {
    opacity: 0.7;
    box-shadow: 0 0 10px currentColor, 0 0 20px currentColor, 0 0 30px currentColor;
  }
}

@keyframes glow-intense {
  0%, 100% {
    opacity: 1;
    box-shadow: 0 0 10px currentColor, 0 0 20px currentColor, 0 0 30px currentColor, 0 0 40px currentColor;
  }
  50% {
    opacity: 0.9;
    box-shadow: 0 0 20px currentColor, 0 0 40px currentColor, 0 0 60px currentColor, 0 0 80px currentColor;
  }
}

@keyframes system-active {
  0%, 100% {
    opacity: 1;
    transform: scale(1);
  }
  50% {
    opacity: 0.95;
    transform: scale(1.01);
  }
}

@keyframes file-transfer {
  0% {
    transform: translateX(-100%);
    opacity: 0;
  }
  50% {
    opacity: 1;
  }
  100% {
    transform: translateX(100%);
    opacity: 0;
  }
}

@keyframes background-shift {
  0%, 100% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
}

@keyframes data-flow {
  0% {
    transform: translateY(0) scale(1);
    opacity: 0.8;
  }
  50% {
    transform: translateY(-10px) scale(1.05);
    opacity: 1;
  }
  100% {
    transform: translateY(0) scale(1);
    opacity: 0.8;
  }
}

import { type ClassValue, clsx } from "clsx";
import { twMerge } from "tailwind-merge";

/**
 * Combines class names using clsx and tailwind-merge.
 * Useful for conditionally applying Tailwind CSS classes.
 *
 * @param inputs - Class values to combine
 * @returns Merged class string
 */
export const cn = (...inputs: ClassValue[]): string => {
  return twMerge(clsx(inputs));
};

import { Slot } from "@radix-ui/react-slot";
import { cva, type VariantProps } from "class-variance-authority";
import * as React from "react";

import { cn } from "@/lib/utils";

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0",
  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground hover:bg-primary/90",
        destructive: "bg-destructive text-destructive-foreground hover:bg-destructive/90",
        outline: "border border-input bg-background hover:bg-accent hover:text-accent-foreground",
        secondary: "bg-secondary text-secondary-foreground hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-10 px-4 py-2",
        sm: "h-9 rounded-md px-3",
        lg: "h-11 rounded-md px-8",
        icon: "h-10 w-10",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  }
);

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean;
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button";
    return (
      <Comp className={cn(buttonVariants({ variant, size, className }))} ref={ref} {...props} />
    );
  }
);
Button.displayName = "Button";

export { Button, buttonVariants };

import { cva, type VariantProps } from "class-variance-authority";
import * as React from "react";

import { cn } from "@/lib/utils";

/**
 * CLI Interfaceのバリアント定義
 * 大文字・等幅フォントを使用したCLI風インターフェース
 */
const cliInterfaceVariants = cva(
  "font-cli-uppercase bg-lcars-dark border-2 border-lcars-green text-lcars-green p-4 rounded-lg",
  {
    variants: {
      variant: {
        default: "border-lcars-green text-lcars-green",
        hacker: "border-lcars-green text-lcars-green shadow-[0_0_10px_rgba(74,255,158,0.3)]",
        retro: "border-lcars-gray text-lcars-gray",
        warning: "border-lcars-yellow text-lcars-yellow",
        error: "border-lcars-red text-lcars-red",
      },
      size: {
        sm: "text-xs p-2",
        default: "text-sm p-4",
        lg: "text-base p-6",
      },
      glow: {
        none: "",
        subtle: "shadow-[0_0_5px_currentColor]",
        normal: "shadow-[0_0_10px_currentColor,0_0_20px_currentColor]",
        intense: "animate-glow-pulse shadow-[0_0_15px_currentColor,0_0_30px_currentColor]",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
      glow: "normal",
    },
  }
);

/**
 * CLI InterfaceコンポーネントのProps
 */
export interface CLIInterfaceProps
  extends Omit<React.HTMLAttributes<HTMLDivElement>, "content">,
    VariantProps<typeof cliInterfaceVariants> {
  /**
   * コマンドラインのプロンプト記号
   */
  prompt?: string;
  /**
   * 表示するコマンドまたは出力
   */
  content: string | string[];
  /**
   * カーソルを表示するか
   */
  showCursor?: boolean;
  /**
   * カーソルの点滅アニメーション
   */
  cursorBlink?: boolean;
}

/**
 * SF映画のインタフェースデザインをインスパイアしたCLIインターフェースコンポーネント
 *
 * 大文字・等幅フォントを使用したレトロなCLI風UIを提供します。
 * 『エイリアン』のマザーコンピューターや『ウォー・ゲーム』の軍事システムのように、
 * 古いシステムや専門家がシステムの深層部にアクセスしている場面を演出します。
 *
 * 原則：
 * - 大文字・等幅フォント（CLI）：レトロと専門性の演出
 * - あえて洗練されていないタイポグラフィを用いることで、事態の深刻さや高度な専門性を表現
 *
 * @example
 * ```tsx
 * <CLIInterface
 *   variant="hacker"
 *   prompt="$"
 *   content="SYSTEM ACCESS GRANTED"
 *   showCursor
 * />
 * ```
 */
const CLIInterface = React.forwardRef<HTMLDivElement, CLIInterfaceProps>(
  (
    {
      className,
      variant,
      size,
      glow,
      prompt = ">",
      content,
      showCursor = false,
      cursorBlink = true,
      ...props
    },
    ref
  ) => {
    const lines = Array.isArray(content) ? content : [content];

    return (
      <div
        ref={ref}
        className={cn(cliInterfaceVariants({ variant, size, glow, className }))}
        {...props}
      >
        <div className="space-y-1">
          {lines.map((line, index) => (
            <div key={`line-${index}-${line.slice(0, 10)}`} className="flex items-start gap-2">
              {prompt && (
                <span className="text-lcars-green-glow shrink-0">
                  {prompt}
                </span>
              )}
              <span className="flex-1 whitespace-pre-wrap wrap-break-word">{line}</span>
            </div>
          ))}
          {showCursor && (
            <div className="flex items-start gap-2">
              {prompt && (
                <span className="text-lcars-green-glow shrink-0">
                  {prompt}
                </span>
              )}
              <span
                className={cn(
                  "inline-block w-2 h-4 bg-lcars-green",
                  cursorBlink && "animate-pulse"
                )}
                role="presentation"
              />
            </div>
          )}
        </div>
      </div>
    );
  }
);
CLIInterface.displayName = "CLIInterface";

export { CLIInterface, cliInterfaceVariants };

import { cva, type VariantProps } from "class-variance-authority";
import * as React from "react";

import { cn } from "@/lib/utils";

/**
 * Clock Displayのバリアント定義
 * LCARS風のデジタル時計表示コンポーネント
 */
const clockDisplayVariants = cva(
  "font-sans tracking-wider transition-all duration-300 tabular-nums",
  {
    variants: {
      variant: {
        default: "text-lcars-blue",
        orange: "text-lcars-orange",
        red: "text-lcars-red",
        yellow: "text-lcars-yellow",
      },
      size: {
        sm: "text-lg",
        default: "text-2xl",
        lg: "text-4xl",
        xl: "text-6xl",
      },
      glow: {
        none: "",
        subtle: "text-shadow-subtle",
        normal: "text-shadow-normal",
        intense: "text-shadow-intense animate-glow-pulse",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
      glow: "normal",
    },
  }
);

/**
 * Clock DisplayコンポーネントのProps
 */
export interface ClockDisplayProps
  extends Omit<React.HTMLAttributes<HTMLDivElement>, "children">,
    VariantProps<typeof clockDisplayVariants> {
  /**
   * 24時間形式で表示するか（true: 24時間、false: 12時間）
   */
  use24Hour?: boolean;
  /**
   * 秒を表示するか
   */
  showSeconds?: boolean;
  /**
   * 日付を表示するか
   */
  showDate?: boolean;
  /**
   * カスタム時刻（指定しない場合は現在時刻）
   */
  customTime?: Date;
  /**
   * ラベルテキスト
   */
  label?: string;
}

/**
 * SF映画のLCARSインタフェースをインスパイアしたデジタル時計表示コンポーネント
 *
 * 24時間/12時間表示、秒表示、日付表示をサポートします。
 * LCARS風のデジタル時計デザインと発光効果でSF的な雰囲気を演出します。
 *
 * @example
 * ```tsx
 * <ClockDisplay use24Hour showSeconds />
 * <ClockDisplay use24Hour={false} showDate label="Stardate" />
 * ```
 */
const ClockDisplay = React.forwardRef<HTMLDivElement, ClockDisplayProps>(
  (
    {
      className,
      variant,
      size,
      glow,
      use24Hour = true,
      showSeconds = false,
      showDate = false,
      customTime,
      label,
      ...props
    },
    ref
  ) => {
    const [currentTime, setCurrentTime] = React.useState<Date>(
      customTime || new Date()
    );

    React.useEffect(() => {
      if (customTime) {
        setCurrentTime(customTime);
        return;
      }

      const timer = setInterval(() => {
        setCurrentTime(new Date());
      }, 1000);

      return () => clearInterval(timer);
    }, [customTime]);

    const formatTime = React.useCallback(
      (date: Date): string => {
        let hours = date.getHours();
        const minutes = date.getMinutes().toString().padStart(2, "0");
        const seconds = date.getSeconds().toString().padStart(2, "0");

        if (!use24Hour) {
          const ampm = hours >= 12 ? "PM" : "AM";
          hours = hours % 12 || 12;
          const timeString = `${hours.toString().padStart(2, "0")}:${minutes}${showSeconds ? `:${seconds}` : ""} ${ampm}`;
          return timeString;
        }

        const timeString = `${hours.toString().padStart(2, "0")}:${minutes}${showSeconds ? `:${seconds}` : ""}`;
        return timeString;
      },
      [use24Hour, showSeconds]
    );

    const formatDate = React.useCallback((date: Date): string => {
      const year = date.getFullYear();
      const month = (date.getMonth() + 1).toString().padStart(2, "0");
      const day = date.getDate().toString().padStart(2, "0");
      return `${year}-${month}-${day}`;
    }, []);

    return (
      <div ref={ref} className={cn("flex flex-col gap-1", className)} {...props}>
        {label && (
          <span
            className={cn(
              clockDisplayVariants({ variant, size: "sm", glow: "subtle" }),
              "uppercase tracking-wider text-xs"
            )}
          >
            {label}
          </span>
        )}
        <div className="flex flex-col gap-1">
          <span
            className={cn(
              clockDisplayVariants({ variant, size, glow }),
              "font-bold"
            )}
          >
            {formatTime(currentTime)}
          </span>
          {showDate && (
            <span
              className={cn(
                clockDisplayVariants({ variant, size: "sm", glow: "subtle" }),
                "opacity-80 text-xs"
              )}
            >
              {formatDate(currentTime)}
            </span>
          )}
        </div>
      </div>
    );
  }
);
ClockDisplay.displayName = "ClockDisplay";

export { ClockDisplay, clockDisplayVariants };

import { cva, type VariantProps } from "class-variance-authority";
import * as React from "react";

import { cn } from "@/lib/utils";
import { LCARSPanel, type LCARSPanelProps } from "./lcars-panel";

/**
 * Connected Panelsのバリアント定義
 * 流れるように接続されたパネルデザイン
 */
const connectedPanelsVariants = cva("relative font-lcars", {
  variants: {
    direction: {
      horizontal: "flex flex-row items-center",
      vertical: "flex flex-col items-center",
    },
    connection: {
      none: "",
      curve: "before:content-[''] before:absolute before:border-2 before:border-lcars-blue before:rounded-full",
      line: "before:content-[''] before:absolute before:border before:border-lcars-blue",
    },
  },
  defaultVariants: {
    direction: "horizontal",
    connection: "curve",
  },
});

/**
 * Connected PanelsコンポーネントのProps
 */
export interface ConnectedPanelsProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof connectedPanelsVariants> {
  /**
   * 接続されるパネルの配列
   */
  panels: Array<{
    /**
     * パネルのコンテンツ
     */
    content: React.ReactNode;
    /**
     * パネルのプロパティ
     */
    panelProps?: Omit<LCARSPanelProps, "children">;
  }>;
  /**
   * パネル間の接続線のスタイル
   */
  connectorStyle?: "solid" | "dashed" | "glow";
}

/**
 * SF映画のLCARSインタフェースをインスパイアした接続されたパネルコンポーネント
 *
 * 要素が自然に接続される視覚効果とカーブした接続線を特徴とします。
 * モジュール式レイアウトでSF的な雰囲気を演出します。
 *
 * @example
 * ```tsx
 * <ConnectedPanels
 *   direction="horizontal"
 *   panels={[
 *     { content: "Panel 1", panelProps: { variant: "default" } },
 *     { content: "Panel 2", panelProps: { variant: "orange" } },
 *   ]}
 * />
 * ```
 */
const ConnectedPanels = React.forwardRef<HTMLDivElement, ConnectedPanelsProps>(
  ({ className, direction, connection, panels, connectorStyle = "glow", ...props }, ref) => {
    return (
      <div
        ref={ref}
        className={cn(connectedPanelsVariants({ direction, connection }), className)}
        {...props}
      >
        {panels.map((panel, index) => (
          <React.Fragment key={index}>
            <LCARSPanel {...panel.panelProps}>{panel.content}</LCARSPanel>
            {index < panels.length - 1 && (
              <div
                className={cn(
                  "relative",
                  direction === "horizontal" ? "w-8 h-0.5" : "h-8 w-0.5",
                  connectorStyle === "glow" &&
                    "bg-lcars-blue shadow-[0_0_10px_rgba(74,158,255,0.5)]",
                  connectorStyle === "dashed" && "border-dashed border-lcars-blue",
                  connectorStyle === "solid" && "bg-lcars-blue"
                )}
              >
                {connection === "curve" && (
                  <svg
                    className={cn(
                      "absolute",
                      direction === "horizontal"
                        ? "w-8 h-8 -top-4 left-0"
                        : "w-8 h-8 -left-4 top-0"
                    )}
                    viewBox="0 0 32 32"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <path
                      d={
                        direction === "horizontal"
                          ? "M 0 16 Q 16 0 32 16"
                          : "M 16 0 Q 0 16 16 32"
                      }
                      stroke="rgb(74, 158, 255)"
                      strokeWidth="2"
                      fill="none"
                      className={cn(
                        connectorStyle === "glow" &&
                          "drop-shadow-[0_0_10px_rgba(74,158,255,0.5)]"
                      )}
                    />
                  </svg>
                )}
              </div>
            )}
          </React.Fragment>
        ))}
      </div>
    );
  }
);
ConnectedPanels.displayName = "ConnectedPanels";

export { ConnectedPanels, connectedPanelsVariants };

import { cva, type VariantProps } from "class-variance-authority";
import * as React from "react";

import { cn } from "@/lib/utils";

/**
 * Data Displayのバリアント定義
 * LCARS風の数値表示コンポーネント
 */
const dataDisplayVariants = cva(
  "font-lcars tracking-wide transition-all duration-300",
  {
    variants: {
      variant: {
        default: "text-lcars-blue",
        orange: "text-lcars-orange",
        red: "text-lcars-red",
        yellow: "text-lcars-yellow",
        purple: "text-lcars-purple",
        gray: "text-lcars-gray",
        green: "text-lcars-green",
      },
      size: {
        sm: "text-sm",
        default: "text-base",
        lg: "text-lg",
        xl: "text-xl",
        "2xl": "text-2xl",
        "3xl": "text-3xl",
        "4xl": "text-4xl",
      },
      glow: {
        none: "",
        subtle: "text-shadow-subtle",
        normal: "text-shadow-normal",
        intense: "text-shadow-intense",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
      glow: "normal",
    },
  }
);

/**
 * Data DisplayコンポーネントのProps
 */
export interface DataDisplayProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof dataDisplayVariants> {
  /**
   * 表示する値
   */
  value: number | string;
  /**
   * ラベルテキスト
   */
  label?: string;
  /**
   * 単位テキスト
   */
  unit?: string;
  /**
   * 数値のフォーマット関数
   */
  formatValue?: (value: number | string) => string;
}

/**
 * SF映画のLCARSインタフェースをインスパイアした数値表示コンポーネント
 *
 * 大きな数字、発光ラベル、単位表示を特徴とします。
 * LCARS風の数値フォーマットと発光効果でSF的な雰囲気を演出します。
 *
 * @example
 * ```tsx
 * <DataDisplay value={1009} label="Warp Core" unit="%" />
 * <DataDisplay value={7490} label="Power" unit="MW" size="2xl" glow="intense" />
 * ```
 */
const DataDisplay = React.forwardRef<HTMLDivElement, DataDisplayProps>(
  (
    { className, variant, size, glow, value, label, unit, formatValue, ...props },
    ref
  ) => {
    const formattedValue = React.useMemo(() => {
      if (formatValue) {
        return formatValue(value);
      }
      if (typeof value === "number") {
        return value.toLocaleString();
      }
      return value;
    }, [value, formatValue]);

    const valueSize: "sm" | "default" | "lg" | "xl" | "2xl" | "3xl" | "4xl" =
      size === "sm"
        ? "lg"
        : size === "default"
          ? "xl"
          : size === "lg"
            ? "2xl"
            : size === "xl"
              ? "3xl"
              : size === "2xl"
                ? "4xl"
                : "4xl";

    return (
      <div ref={ref} className={cn("flex flex-col gap-1", className)} {...props}>
        {label && (
          <span
            className={cn(
              dataDisplayVariants({ variant, size: "sm", glow: "subtle" }),
              "text-hierarchy-secondary"
            )}
          >
            {label}
          </span>
        )}
        <div className="flex items-baseline gap-2">
          <span
            className={cn(
              dataDisplayVariants({ variant, size: valueSize, glow }),
              "font-bold tabular-nums"
            )}
          >
            {formattedValue}
          </span>
          {unit && (
            <span
              className={cn(
                dataDisplayVariants({ variant, size: "sm", glow: "subtle" }),
                "opacity-80"
              )}
            >
              {unit}
            </span>
          )}
        </div>
      </div>
    );
  }
);
DataDisplay.displayName = "DataDisplay";

export { DataDisplay, dataDisplayVariants };

import { cva, type VariantProps } from "class-variance-authority";
import * as React from "react";

import { cn } from "@/lib/utils";
import { LCARSPanel, type LCARSPanelProps } from "./lcars-panel";

/**
 * Display Panelのバリアント定義
 * 透過/半透明効果付きのLCARS風ディスプレイパネル
 */
const displayPanelVariants = cva("", {
  variants: {
    transparency: {
      none: "bg-lcars-dark/95",
      subtle: "bg-lcars-dark/80 backdrop-blur-sm",
